import React from 'react';
import { useState, useEffect } from 'react';
// import { MenuItems } from './MenuItems';
import './Navbar.css';
import { NavLink, Redirect } from 'react-router-dom';
// import LocationDropdown from './LocationDropdown';
// import Searchbar from './Searchbar';
// import { Signinbutton } from './Signinbutton';

const settings = ['Profile', 'Account', 'Dashboard', 'Logout'];


const Navbar = () => {
    // const state={clicked: false}
    // const [state, setState] = useState(false);

    // const handleClick = () => {
    //     setState(current => !current);
    //     console.log('clicked');
    // }



    //using useEffect to get the token status
    const [loggedIn, setLoggedIn] = useState(false);
    const tokenStatus = () => {
        let response = localStorage.getItem('token');
        console.log(response);
        if (response !== null) {
            setLoggedIn(true);
        }

        // setToken(response.data);
    };

    useEffect(() => {
        tokenStatus();
    }, []);
    const onClickSellcar = () => {
        if (loggedIn) {
            return <Redirect to='/sellcar' />
        } else {
            return <Redirect to='/login' />
        }
    }

    const token = localStorage.getItem('token');
    if (token) {
        console.log('token present');
        console.log(token);
        // const loggedIn = true;
        // setLoggedIn(true);
    } else {
        const token = false;
        // const loggedIn = false;
        console.log('token absent');
        console.log(token);
    }

    const logout = () => {
        localStorage.removeItem('token');
        window.location.reload('/');
    }


    return (
        <>
            {/* <nav className="navbarItems">
                <h1 className="navbar-logo">SellYourCar<i className="fa fa-car"></i></h1>
                <div className="menu-icon" onClick={handleClick}>
                    <i className={state ? 'fas fa-times' : 'fas fa-bars'}></i>

                </div>
                <ul className={state ? 'nav-menu active ' : 'nav-menu '} >
                    
                    <li >
                        <Searchbar />
                    </li>

                </ul>
                <ul className={state ? 'nav-menu active ' : 'nav-menu '}>
                    {MenuItems.map((item, index) => {
                        return (
                            <li key={index} >
                                <NavLink className={item.cName} to={item.url} >
                                    {item.title}
                                </NavLink>
                            </li>


                        )
                    })}
                </ul>
                <Signinbutton>SignIn</Signinbutton>
            </nav> */}


            <input type="checkbox" id="check" />
            <nav >
                <NavLink to="/" className="icon"><b style={{ color: 'red' }}>S</b>ell<b style={{ color: 'red' }}>C</b>ar</NavLink>
                <div className="search_box">
                    <input type="search" placeholder="Search Car" />
                    <span className="fa fa-search"></span>
                </div>
                <ol>
                    <li><NavLink to="/sellcar">SellCar</NavLink></li>
                    {/* <li><NavLink onClick={onClickSellcar} >SellCar</NavLink></li> */}
                    {/* <li><NavLink to="/login">Login</NavLink></li>  */}

                    {token ? <li><NavLink to="/" onClick={logout}>Logout</NavLink></li> : <li><NavLink to="/login" >Login</NavLink></li>}
                    <Box sx={{ flexGrow: 0 }}>
            <Tooltip title="Open settings">
              <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                <Avatar alt="Remy Sharp" src="/static/images/avatar/2.jpg" />
              </IconButton>
            </Tooltip>
            <Menu
              sx={{ mt: '45px' }}
              id="menu-appbar"
              anchorEl={anchorElUser}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={Boolean(anchorElUser)}
              onClose={handleCloseUserMenu}
            >
              {settings.map((setting) => (
                <MenuItem key={setting} onClick={handleCloseNavMenu}>
                  <Typography textAlign="center">{setting}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box>

                </ol>
                <label for="check" className="bar">
                    <span className="fa fa-bars" id="bars"></span>
                    <span className="fa fa-times" id="times"></span>
                </label>
            </nav>
        </>
    )
}

export default Navbar;
