import React, { useEffect, useState } from 'react';
import { fetchAllUsers } from '../../../api/Api.js';
import Navbar from '../AdminNavbar/AdminNavbar';
import {
    Table,
    TableHead,
    TableCell,
    TableRow,
    Button,
    TableBody,
    makeStyles,
} from "@material-ui/core";
import { Link } from "react-router-dom";

const useStyles = makeStyles({
    table: {
        width: "90%",
        margin: "50px 0 0 50px",
    },
    thead: {
        "& > *": {
            fontSize: 20,
            background: "#000000",
            color: "#FFFFFF",
        },
    },
    row: {
        "& > *": {
            fontSize: 18,
        },
    },
});




const Userlist = () => {

    const [data, setData] = useState([]);
    const [users, setUsers] = useState([]);
    const classes = useStyles();


    const fetchallusers = async () => {
        const responce = await fetchAllUsers();
        // console.log(responce);
        console.log(responce.data);
        const allUsers = responce.data;
        setUsers( allUsers );
        // console.log(data);
    }

    useEffect(() => {
        fetchallusers();
    }, []);

    console.log("users ", users);
    let sr_no = 1;



    return (
        <>
        <Navbar/>
        <Table className={classes.table}>
                <TableHead>
                    <TableRow className={classes.thead}>
                        <TableCell>Sr.No.</TableCell>
                        {/* <TableCell>UserId</TableCell> */}
                        <TableCell>Username</TableCell>
                        <TableCell>FullName</TableCell>
                        <TableCell>Address</TableCell>
                        <TableCell>Email</TableCell>
                        <TableCell>Phone</TableCell>
                        <TableCell>Cars</TableCell>
                        <TableCell>Membership</TableCell>
                        <TableCell>Membership Expiry</TableCell>
                        <TableCell>Action</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {users.map((user) => (
                        <TableRow className={classes.row}>
                            <TableCell>{sr_no++}</TableCell>
                            {/* <TableCell>{user._id}</TableCell> */}
                            <TableCell>{user.username}</TableCell>
                            <TableCell>{user.fullname}</TableCell>
                            <TableCell>{user.district } ({user.state})</TableCell>
                            <TableCell>{user.email}</TableCell>
                            <TableCell>{user.phone}</TableCell>
                            <TableCell>
                                <Button
                                    color="primary"
                                    variant="contained"
                                    style={{ marginRight: 10 }}
                                    component={Link}
                                    to={`/edit/${user._id}`}
                                >
                                    Edit
                                </Button>{" "}
                                {/* change it to user.id to use JSON Server */}
                                <Button
                                    color="secondary"
                                    variant="contained"
                                    // onClick={() => deleteUserData(user._id)}
                                >
                                    Delete
                                </Button>{" "}
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </>
    )
}

export default Userlist;
