import React from 'react';
import { Grid, Paper, Avatar, TextField, Checkbox, Button } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Link from 'react-router-dom/Link';
import Typography from '@material-ui/core/Typography';
import {InputLabel, Input, FormControl} from '@material-ui/core/';

const Signup = () => {


    const paperStyle = { padding: '20px', height: '60vh', width: 380, margin: '20px auto' }
    const avatarStyle = { backgroundColor: 'green' }
    const btnStyle = { margin: '20px auto' }



    const onValueChange = (e) => {
                console.log(e.target.value);
            }


    return (
        <>
            <Grid>
                <Paper elevation={10} style={paperStyle}>
                    <Grid align="center">
                        <Avatar style={avatarStyle}><LockOutlinedIcon /></Avatar>
                        <h2>Signup</h2>
                    </Grid>
                    <TextField id="standard-basic" label="Username" placeholder="Enter Username" fullWidth required />
                    <TextField id="standard-basic" label="Password" placeholder="Enter Password" type="password"  required />
                    <div>
                    <TextField id="standard-basic" label="Password" placeholder="Enter Password" type="password"  required />
                    <TextField id="standard-basic" label="Password" placeholder="Enter Password" type="password"  required />
                    </div>
                    






                    <div>

                    </div>
                    
                    <FormControl>
                        <InputLabel htmlFor="my-input">Mobile Number</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='mobilenumber'  id="my-input" />
                    </FormControl>
                    <FormControl>
                        <InputLabel htmlFor="my-input">Email</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='email' id="my-input" />
                    </FormControl>
                    <FormControl>
                        <InputLabel htmlFor="my-input">Username</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='username'  id="my-input" />
                    </FormControl>








                    <FormControlLabel
                        label="Remember me"
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                    />
                    <Button type="submit" color="primary" variant="contained" style={btnStyle} fullWidth>Login</Button>
                    <Typography color="textSecondary">
                        <Link to="/forgotpassword">Forgot Password</Link>
                    </Typography>
                    <Typography color="textSecondary" >
                        Don't have an Account  ?
                        <Link to="/signup"> Sign Up</Link>
                    </Typography>
                </Paper>
            </Grid>
        </>
    )
}

export default Signup;
