import React, {useState} from 'react';
import { FormGroup, FormControl, InputLabel, Input, Button, makeStyles, Typography } from '@material-ui/core';
import Container from '@material-ui/core/Container';
import NavLink from 'react-router-dom/';
import { addUser } from '../../api/Api';
import { useHistory } from 'react-router-dom';





const initialValue = {
    fullname: '',
    address: '',
    mobilenumber: '',
    email: '',
    username: '',
    password: ''
}

const useStyles = makeStyles({
    container: {
        backgroundColor: '#2E8BC0',
        padding: '20px',
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 10,
        },
        shadowOpacity: 0.12,
        shadowRadius: 60,
        width: '50%',
        border: 'black 2px',
        margin: '5% 0 0 25%',
        '& > *': {
            marginTop: 20
        }
    },
    ptag:{
        align: 'center',
    }
})

const Signup = () => {
    const [user, setUser] = useState(initialValue);
    const { fullname, address, mobilenumber, email, username, password  } = user;
    const classes = useStyles();
    let history = useHistory();

    const onValueChange = (e) => {
        console.log(e.target.value);
        setUser({...user, [e.target.name]: e.target.value})
    }

    const addUserDetails = async() => {
        await addUser(user);
        history.push('/');
    }



    return (
        <>


            <Container maxWidth="md" >
                <FormGroup className={classes.container}>
                    <Typography variant="h4">SignUp</Typography>

                    <FormControl>
                        <InputLabel htmlFor="my-input">Full Name</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='fullname' value={fullname} id="my-input" />
                    </FormControl>
                    <FormControl>
                        <InputLabel htmlFor="my-input">Address</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='address' value={address} id="my-input" />
                    </FormControl>
                    <FormControl>
                        <InputLabel htmlFor="my-input">Mobile Number</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='mobilenumber' value={mobilenumber} id="my-input" />
                    </FormControl>
                    <FormControl>
                        <InputLabel htmlFor="my-input">Email</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='email' value={email} id="my-input" />
                    </FormControl>
                    <FormControl>
                        <InputLabel htmlFor="my-input">Username</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='username' value={username} id="my-input" />
                    </FormControl>
                    <FormControl>
                        <InputLabel htmlFor="my-input">Password</InputLabel>
                        <Input onChange={(e) => onValueChange(e)} name='password' value={password} id="my-input" />
                    </FormControl>
                    <FormControl>
                        <Button variant="contained" color="secondary" onClick={() => addUserDetails()}>Sign Up</Button>
                    </FormControl>
                    <FormControl>
                        <p className="ptag"><NavLink to="/login">Already have an Account</NavLink></p>
                    </FormControl>
                </FormGroup>
            </Container>
        </>
    )
}

export default Signup;
