import React, { useState } from 'react';
import { Grid, Paper, Avatar, TextField, Button } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import { Link } from 'react-router-dom';
import Typography from '@material-ui/core/Typography';
import { useHistory } from 'react-router-dom';
import { addUser } from '../../api/Api';
import FileBase from 'react-file-base64';







const initialValue = {
    fullname: '',
    district: '',
    state: '',
    mobilenumber: '',
    email: '',
    username: '',
    password: '',
}


const Sellcar = () => {


    const paperStyle = { padding: '20px', height: '100vh', width: 580, margin: '20px auto' }
    const avatarStyle = { backgroundColor: 'green' }
    const btnStyle = { margin: '20px auto' }

    const [user, setUser] = useState(initialValue);
    const { fullname, district, state, mobilenumber, email, username, password } = user;
    let history = useHistory();

    const onValueChange = (e) => {
        console.log(e.target.value);
        setUser({ ...user, [e.target.name]: e.target.value })
    }


  const handleSubmit = (e) => {
      e.preventDefault();
  }
    const addUserDetails = async () => {
        console.log("clicked");
        await addUser(user);
        history.push('/');
        //  console.log(user);
    }

    const clear = () => {

    }



    return (
        <>
            <Grid>
                <Paper elevation={10} style={paperStyle}>
                    <Grid align="center">
                        <Avatar style={avatarStyle}><LockOutlinedIcon /></Avatar>
                        <h2>Signup</h2>
                    </Grid>
                    <form autoComplete="off" noValidate  onSubmit={handleSubmit}>

                        <TextField name="brand" vaariant="outlined" label="brand" value={brand}  fullWidth />
                        <TextField name="model" vaariant="outlined" label="model" value={model}  fullWidth />
                        <TextField name="registrationYear" vaariant="outlined" label="registrationYear" value={brand}  fullWidth />
                        <TextField name="fuelType" vaariant="outlined" label="fuelType" value={brand}  fullWidth />
                        <TextField name="transmissionType" vaariant="outlined" label="transmissionType" value={brand}  fullWidth />
                        <TextField name="registrationState" vaariant="outlined" label="registrationState" value={brand}  fullWidth />
                        <TextField name="price" vaariant="outlined" label="price" value={brand}  fullWidth />
                        <br />
                        <div >
                            <FileBase type="file" multiple={false} />
                        </div><br />
                        <Button variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
                        <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>

                    </form>
                </Paper>
            </Grid>
        </>
    )
}

export default Sellcar;
