import React from 'react';
import { Grid, Paper, Avatar, TextField, Checkbox, Button } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Link from 'react-router-dom/Link';
import Typography from '@material-ui/core/Typography';

const Login = () => {


    const paperStyle = { padding: '20px', height: '70vh', width: 380, margin: '20px auto' }
    const avatarStyle = { backgroundColor: 'green' }
    // const bStyle = { margin: '20px auto' }
    return (
        <>
            <Grid>
                <Paper elevation={10} style={paperStyle}>
                    <Grid align="center">
                        <Avatar style={avatarStyle}><LockOutlinedIcon /></Avatar>
                        <h2>SignIn</h2>
                    </Grid>
                    <TextField id="standard-basic" label="Username" placeholder="Enter Username" fullWidth required />
                    <TextField id="standard-basic" label="Password" placeholder="Enter Password" type="password" fullWidth required />
                    <FormControlLabel
                        label="Remember me"
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                    />
                    <Button type="submit" color="primary" variant="contained" style={bStyle} fullWidth>Login</Button>
                    <Typography variant="body2" color="textSecondary">
                        <Link to="/forgotpassword">Forgot Password</Link>
                    </Typography>
                    <Typography variant="body2" color="textSecondary" >
                        Don't have an Account
                        <Link to="/signup">Sign Up</Link>
                    </Typography>
                </Paper>
            </Grid>
        </>
    )
}

export default Login;
