import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

import { styled } from '@mui/material/styles';
import IconButton from '@mui/material/IconButton';
import TextareaAutosize from '@mui/material/TextareaAutosize';





import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    pt: 2,
    px: 4,
    pb: 3,
};




const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
})(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
    }),
}));


const Cardetail = (car) => {

    // const [cardetail, setCardetails] = useState(car.location.state.car);
    const cardetail = car.location.state.car;
    // console.log(cardetail);

    // console.log("user info", cardetail.ownerDetails);
    const ownerDetails = cardetail.ownerDetails.user;




    useEffect(() => {
        // fetchCarDetails();
    }, []);






    //olx api
    // const searchcarname = cardetail.model.replace(/ /g, '')
    // console.log(searchcarname);

    // const olxUrl = "https://www.olx.in/items/q-"+searchcarname+"?isSearchCall=true";
    // console.log(olxUrl);
    // useEffect(() => {
    //     carDetailsWithFetch();
    // }, []);

    // const carDetailsWithFetch = async () => {
    //     const response = await fetch(olxUrl);
    //     const jsonData = await response.json();
    //     console.log(jsonData);
    // };







    function createData(name, calories, fat, carbs, protein) {
        return { name, calories, fat, carbs, protein };
    }

    const rows = [
        createData('Mileage ', 159, 6.0, 24, 4.0),
        createData('Ground Clearance ', 237, 9.0, 37, 4.3),
        createData('Engine Type', 262, 16.0, 24, 6.0),
        createData('Fuel Type', 305, 3.7, 67, 4.3),
        createData('Transmission', 356, 16.0, 49, 3.9),
        createData('Airbags', 356, 16.0, 49, 3.9),
        createData('Gingerbread', 356, 16.0, 49, 3.9),
        createData('Gingerbread', 356, 16.0, 49, 3.9),
    ];




    const [expanded, setExpanded] = useState(false);
    const [open, setOpen] = useState(false);
    const [openreport, setOpenreport] = useState(false);
    const [reportMessage, setReportMessage] = useState('');


    const handleReadMoreClick = async () => {
        console.log("clicked")
        const token = localStorage.getItem('token')
        // console.log(token)
        if (token) {
            const membership = await membershipInfo(token);
            const user = membership.data;
            var newDate = new Date().toISOString();
            if (user.membership === true && user.membershipexpiry > newDate) {
                setExpanded(!expanded);
            } else {
                alert("You are not a member")
                setOpen(true);
            }
        } else {
            console.log("no token")
            alert("login first")
        }
    };

    const handleAccordionExpandClick = async () => {
        console.log("accordion clicked")
        const token = localStorage.getItem('token')
        // console.log(token)
        if (token) {
            const membership = await membershipInfo(token);
            // console.log(membership.data)
            const user = membership.data;
            var newDate = new Date().toISOString();
            if (user.membership === true && user.membershipexpiry > newDate) {
                setExpanded(!expanded);
            } else {
                alert("You are not a member")
                setOpen(true);
            }

        } else {
            console.log("no token")
            alert("login first")
        }
    };


    const handleClose = () => {
        setOpen(false);
    };

    const handleCloseReport = () => {
        setOpenreport(false);
    };


    const getmembership = async () => {
        console.log(" get membership clicked")
        const token = localStorage.getItem('token')
        // console.log(token)
        const response = await updateMembership(token);
        // console.log(response)
        // console.log(response.data)
        if (response.data === 'membership updated') {
            setOpen(false);
            alert("congrats!! You are a premium member now. ")
        }



    }


    const openReportForm = () => {
        console.log("clicked")
        const token = localStorage.getItem('token')
        // console.log(token)
        if (token) {
            setOpenreport(true);
        } else {
            console.log("no token")
            alert("login first")
        }
    }

    const reportCar = async () => {
        console.log("report car clicked")
        const token = localStorage.getItem('token')
        console.log(reportMessage)
        // console.log(token)
        const carId = cardetail._id;
        console.log(carId)
        const response = await reportCarDetails(token, carId, reportMessage);
        // console.log(response)
        console.log(response.data)
        setOpenreport(false);
        if (response.data === 'car reported') {
            alert("car reported successfully")
        }
    }

    return (
        <>
            <Navbar />
            <Grid container justify="space-between" spacing={2} style={{ padding: '30px', width: "100%", marginBottom: 'none', backgroundColor: "#7900FF" }} >
                <Grid item xs={12} md={8} >
                    <Card container style={{ color: 'red', backgroundColor: '#f1f1f1' }} >
                        <CardMedia
                            component="img"
                            height="490"
                            width="100px"
                            image={cardetail.carImages}
                            alt="Car img"
                        />
                    </Card>
                </Grid>
                <Grid item xs={12} md={4}  >
                    <Card style={{ backgroundColor: '#757ce8' }} >
                        <CardContent >
                            <Typography gutterBottom variant="h4"  >
                                Description
                                <Button variant="contained" color="success" style={{ float: 'right', color: "red" }} onClick={openReportForm}>Report</Button>
                                <Modal
                                    open={openreport}
                                    onClose={handleCloseReport}
                                    aria-labelledby="parent-modal-title"
                                    aria-describedby="parent-modal-description"
                                >
                                    <Box sx={{ ...style, width: 500 }}>
                                        <TextareaAutosize
                                            aria-label="minimum height"
                                            minRows={3}
                                            placeholder="Enter Report Description"
                                            style={{ width: 430 }}
                                            onChange={(e) => setReportMessage(e.target.value)}
                                        />
                                        <br></br>
                                        <Button size="small" onClick={reportCar}>Report Car</Button>
                                    </Box>
                                </Modal>


                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Brand Name: {cardetail.brand}
                            </Typography>

                            <Typography gutterBottom variant="h6" >
                                Model Name:  {cardetail.model}
                            </Typography>

                            <Typography gutterBottom variant="h6" >
                                Registration Year:  {cardetail.registrationYear}
                            </Typography>

                            <Typography gutterBottom variant="h6" >
                                Transmission Type:  {cardetail.transmissionType}
                            </Typography>

                            <Typography gutterBottom variant="h6" >
                                Fuel Type:  {cardetail.fuelType}
                            </Typography>

                            
                            <Typography variant="body2" color="text.secondary">
                            {cardetail.description}
                            </Typography>

                        </CardContent>
                        <CardActions>
                            <Button size="small" onClick={handleReadMoreClick}>Read More</Button>
                            {/* <Button size="small" onClick={handleOpen}>Read More</Button> */}
                        </CardActions>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="parent-modal-title"
                            aria-describedby="parent-modal-description"
                        >
                            <Box sx={{ ...style, width: 500 }}>
                                <h2 id="parent-modal-title">You want to get membership?</h2><br></br>
                                <p id="parent-modal-description">
                                    Pay Rs.500/- and get membership for 1 year
                                </p><br></br>
                                <Button size="small" onClick={getmembership}>Get Membership</Button>
                            </Box>
                        </Modal>


                        <Collapse in={expanded} timeout="auto" unmountOnExit>
                            <CardContent>
                            <Typography gutterBottom variant="h6" >
                                Accelera:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            <Typography gutterBottom variant="h6" >
                                Registration State:  {cardetail.registrationState}
                            </Typography>
                            









                            </CardContent>
                        </Collapse>
                    </Card>

                </Grid>
            </Grid>


            {/* accordion */}
            {/* compare cars */}

            <Grid container item xs={12} md={12} style={{ backgroundColor: "#7900FF", paddingRight: '10px', marginTop: '-30px' }}>
                <Accordion onClick={handleAccordionExpandClick} style={{ margin: '30px', width: "100%", backgroundColor: "blue" }} >
                    <AccordionSummary
                        expandIcon={<ExpandMoreIcon />}

                        aria-controls="panel1a-content"
                        id="panel1a-header"
                    >
                        <Typography>Compare</Typography>
                    </AccordionSummary>
                    <Collapse in={expanded} timeout="auto" unmountOnExit>
                        <AccordionDetails>
                            <TableContainer component={Paper}>
                                <Table sx={{ minWidth: 650 }} aria-label="simple table">
                                    <TableHead>
                                        <TableRow>
                                            <TableCell><h3>{cardetail.model}</h3></TableCell>
                                            <TableCell align="right">SellYourCar</TableCell>
                                            <TableCell align="right">OLX</TableCell>
                                            <TableCell align="right">Carwale</TableCell>
                                            <TableCell align="right">Protein&nbsp;(g)</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {rows.map((row) => (
                                            <TableRow
                                                key={row.name}
                                                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                            >
                                                <TableCell component="th" scope="row">
                                                    {row.name}
                                                </TableCell>
                                                <TableCell align="right">{row.calories}</TableCell>
                                                <TableCell align="right">{row.fat}</TableCell>
                                                <TableCell align="right">{row.carbs}</TableCell>
                                                <TableCell align="right">{row.protein}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </AccordionDetails>
                    </Collapse>
                </Accordion>
            </Grid>
        </>
    )
}

export default Cardetail;
