import React, { useState } from 'react';
import { Grid, Paper, Avatar, TextField, Button } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import { Link } from 'react-router-dom';
import Typography from '@material-ui/core/Typography';
import { useHistory } from 'react-router-dom';
import { addUser } from '../../api/Api';
import FileBase from 'react-file-base64';







const initialValue = {
    fullname: '',
    district: '',
    state: '',
    mobilenumber: '',
    email: '',
    username: '',
    password: '',
}


const Sellcar = () => {


    const paperStyle = { padding: '20px', height: '100vh', width: 580, margin: '20px auto' }
    const avatarStyle = { backgroundColor: 'green' }
    const btnStyle = { margin: '20px auto' }

    const [user, setUser] = useState(initialValue);
    const { fullname, district, state, mobilenumber, email, username, password } = user;
    let history = useHistory();

    const onValueChange = (e) => {
        console.log(e.target.value);
        setUser({ ...user, [e.target.name]: e.target.value })
    }



    const addUserDetails = async () => {
        console.log("clicked");
        await addUser(user);
        history.push('/');
        //  console.log(user);
    }

    const clear = () => {

    }



    return (
        <>
            <Grid>
                <Paper elevation={10} style={paperStyle}>
                    <Grid align="center">
                        <Avatar style={avatarStyle}><LockOutlinedIcon /></Avatar>
                        <h2>Signup</h2>
                    </Grid>

                    <TextField name="brand" vaariant="outlined" label="brand" fullWidth />
                    <TextField name="model" vaariant="outlined" label="model" fullWidth />
                    <TextField name="registrationYear" vaariant="outlined" label="registrationYear" fullWidth />
                    <TextField name="fuelType" vaariant="outlined" label="Tags" fullWidth />
                    <br />
                    <div >
                        <FileBase type="file" multiple={false} />
                    </div><br />
                    <Button variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
                    <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>


                </Paper>
            </Grid>
        </>
    )
}

export default Sellcar;
